import React, { useEffect, useState } from 'react';
import {
  Container,
  Typography,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Button,
  CircularProgress,
  Alert,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import axiosInstance from '../api/axiosInstance';
import Header from '../components/Header';

const UploadedFilesPage = () => {
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [openShareDialog, setOpenShareDialog] = useState(false);
  const [shareableLink, setShareableLink] = useState('');
  const [selectedFileId, setSelectedFileId] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUploadedFiles = async () => {
      try {
        const response = await axiosInstance.get('/api/all-files/');
        setFiles(response.data.files || []);
      } catch (err) {
        console.error('Error fetching files:', err);
        setError('Failed to fetch uploaded files.');
      } finally {
        setLoading(false);
      }
    };
    fetchUploadedFiles();
  }, []);

  const handleDownload = async (encryptedFileId, fileName) => {
    if (!encryptedFileId) {
      alert('Invalid file ID. Unable to download.');
      return;
    }
    try {
      const response = await axiosInstance.get(`/api/access-shared-file/${encryptedFileId}/`, {
        responseType: 'blob',
      });
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', fileName || 'file.encrypted');
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    } catch (err) {
      console.error('Error downloading file:', err);
      alert('Failed to download file.');
    }
  };

  const handleGenerateLink = (encryptedFileId) => {
    const baseUrl = window.location.origin; // Get the base URL of the application
    const link = `${baseUrl}/shared-with/${encryptedFileId}`;
    setShareableLink(link);
    setSelectedFileId(encryptedFileId);
    setOpenShareDialog(true);
  };

  const handleCopyLink = () => {
    navigator.clipboard.writeText(shareableLink).then(() => {
      alert('Link copied to clipboard!');
    });
  };

  const handleViewSharedWith = (encryptedFileId) => {
    navigate(`/shared-with/${encryptedFileId}`);
  };

  return (
    <>
      <Header />
      <Container maxWidth="md" sx={{ mt: 10 }}>
        <Typography variant="h4" gutterBottom>
          Uploaded Files
        </Typography>

        {loading ? (
          <CircularProgress />
        ) : error ? (
          <Alert severity="error">{error}</Alert>
        ) : (
          <TableContainer component={Paper} sx={{ mt: 4 }}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>File Name</TableCell>
                  <TableCell>Uploaded At</TableCell>
                  <TableCell>Actions</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {files.map((file) => (
                  <TableRow key={file.encrypted_file_id}>
                    <TableCell>{file.file_name}</TableCell>
                    <TableCell>{new Date(file.uploaded_at).toLocaleString()}</TableCell>
                    <TableCell>
                      <Button
                        variant="contained"
                        color="primary"
                        onClick={() => handleDownload(file.encrypted_file_id, file.file_name)}
                      >
                        Download
                      </Button>
                      <Button
                        variant="outlined"
                        color="secondary"
                        sx={{ ml: 2 }}
                        onClick={() => handleGenerateLink(file.encrypted_file_id)}
                      >
                        Share
                      </Button>
                      <Button
                        variant="text"
                        color="info"
                        sx={{ ml: 2 }}
                        onClick={() => handleViewSharedWith(file.encrypted_file_id)}
                      >
                        Shared With
                      </Button>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        )}

        {/* Share Dialog */}
        <Dialog open={openShareDialog} onClose={() => setOpenShareDialog(false)}>
          <DialogTitle>Share File</DialogTitle>
          <DialogContent>
            <Typography variant="body1" gutterBottom>
              Share this link with others:
            </Typography>
            <TextField
              fullWidth
              value={shareableLink}
              InputProps={{
                readOnly: true,
              }}
              sx={{ mt: 2 }}
            />
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setOpenShareDialog(false)} color="secondary">
              Close
            </Button>
            <Button onClick={handleCopyLink} color="primary">
              Copy Link
            </Button>
          </DialogActions>
        </Dialog>
      </Container>
    </>
  );
};

export default UploadedFilesPage;
