import React, { useEffect, useState } from 'react';
import {
  Container,
  Typography,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Button,
  TextField,
  Alert,
  CircularProgress,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
} from '@mui/material';
import axiosInstance from '../api/axiosInstance';
import Header from '../components/Header';

const UploadedFilesPage = () => {
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [sharedUsers, setSharedUsers] = useState([]);
  const [openShareDialog, setOpenShareDialog] = useState(false);
  const [openSharedWithDialog, setOpenSharedWithDialog] = useState(false);
  const [selectedFileId, setSelectedFileId] = useState(null);
  const [shareDetails, setShareDetails] = useState({
    user_id: '',
    view_permission: false,
    download_permission: false,
    expiration: 24,
  });

  useEffect(() => {
    const fetchUploadedFiles = async () => {
      try {
        const response = await axiosInstance.get('/api/all-files/');
        setFiles(response.data.files || []);
      } catch (err) {
        console.error('Error fetching files:', err);
        setError('Failed to fetch uploaded files.');
      } finally {
        setLoading(false);
      }
    };
    fetchUploadedFiles();
  }, []);

  const handleDownload = async (encryptedFileId, fileName) => {
    try {
      const response = await axiosInstance.get(`/api/access-shared-file/${encryptedFileId}/`, {
        responseType: 'blob',
      });
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', fileName || 'file.encrypted');
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    } catch (err) {
      console.error('Error downloading file:', err);
      alert('Failed to download file.');
    }
  };

  const openShareDialogHandler = (encryptedFileId) => {
    setSelectedFileId(encryptedFileId);
    setOpenShareDialog(true);
  };

  const handleShareFile = async () => {
    try {
      const response = await axiosInstance.post(`/api/share/${selectedFileId}/`, shareDetails);
      alert(response.data.message || 'File shared successfully.');
      setOpenShareDialog(false);
    } catch (err) {
      console.error('Error sharing file:', err);
      alert('Failed to share file.');
    }
  };

  const handleViewSharedWith = async (encryptedFileId) => {
    try {
      setSelectedFileId(encryptedFileId);
      const response = await axiosInstance.get(`/api/shared-with/${encryptedFileId}/`);
      setSharedUsers(response.data.shared_users || []);
      setOpenSharedWithDialog(true);
    } catch (err) {
      console.error('Error fetching shared users:', err);
      alert('Failed to fetch shared users.');
    }
  };

  const handleRevokeAccess = async (username) => {
    try {
      await axiosInstance.post(`/api/revoke/${selectedFileId}/`, { username });
      alert('Access revoked successfully.');
      setSharedUsers((prev) => prev.filter((user) => user.username !== username));
    } catch (err) {
      console.error('Error revoking access:', err);
      alert('Failed to revoke access.');
    }
  };

  return (
    <>
      <Header />
      <Container maxWidth="md" sx={{ mt: 10 }}>
        <Typography variant="h4" gutterBottom>
          Uploaded Files
        </Typography>

        {loading ? (
          <CircularProgress />
        ) : error ? (
          <Alert severity="error">{error}</Alert>
        ) : (
          <TableContainer component={Paper} sx={{ mt: 4 }}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>File Name</TableCell>
                  <TableCell>Uploaded At</TableCell>
                  <TableCell>Actions</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {files.map((file) => (
                  <TableRow key={file.encrypted_file_id}>
                    <TableCell>{file.file_name}</TableCell>
                    <TableCell>{new Date(file.uploaded_at).toLocaleString()}</TableCell>
                    <TableCell>
                      <Button
                        variant="contained"
                        color="primary"
                        onClick={() => handleDownload(file.encrypted_file_id, file.file_name)}
                      >
                        Download
                      </Button>
                      <Button
                        variant="outlined"
                        color="secondary"
                        onClick={() => openShareDialogHandler(file.encrypted_file_id)}
                      >
                        Share
                      </Button>
                      <Button
                        variant="text"
                        color="info"
                        onClick={() => handleViewSharedWith(file.encrypted_file_id)}
                      >
                        Shared With
                      </Button>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        )}

        {/* Share Dialog */}
        <Dialog open={openShareDialog} onClose={() => setOpenShareDialog(false)}>
          <DialogTitle>Share File</DialogTitle>
          <DialogContent>
            <TextField
              label="User ID"
              fullWidth
              value={shareDetails.user_id}
              onChange={(e) => setShareDetails({ ...shareDetails, user_id: e.target.value })}
              sx={{ mt: 2 }}
            />
            <TextField
              label="Expiration (hours)"
              fullWidth
              type="number"
              value={shareDetails.expiration}
              onChange={(e) => setShareDetails({ ...shareDetails, expiration: e.target.value })}
              sx={{ mt: 2 }}
            />
            <Button
              variant={shareDetails.view_permission ? 'contained' : 'outlined'}
              onClick={() =>
                setShareDetails({ ...shareDetails, view_permission: !shareDetails.view_permission })
              }
              sx={{ mt: 2 }}
            >
              {shareDetails.view_permission ? 'View Enabled' : 'Enable View'}
            </Button>
            <Button
              variant={shareDetails.download_permission ? 'contained' : 'outlined'}
              onClick={() =>
                setShareDetails({
                  ...shareDetails,
                  download_permission: !shareDetails.download_permission,
                })
              }
              sx={{ mt: 2, ml: 2 }}
            >
              {shareDetails.download_permission ? 'Download Enabled' : 'Enable Download'}
            </Button>
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setOpenShareDialog(false)}>Cancel</Button>
            <Button onClick={handleShareFile}>Share</Button>
          </DialogActions>
        </Dialog>

        {/* Shared With Dialog */}
        <Dialog open={openSharedWithDialog} onClose={() => setOpenSharedWithDialog(false)}>
          <DialogTitle>Shared With</DialogTitle>
          <DialogContent>
            {sharedUsers.length > 0 ? (
              sharedUsers.map((user) => (
                <div
                  key={user.username}
                  style={{ display: 'flex', justifyContent: 'space-between', marginTop: 16 }}
                >
                  <Typography>{`${user.username} (${user.email})`}</Typography>
                  <Button
                    variant="outlined"
                    color="error"
                    onClick={() => handleRevokeAccess(user.username)}
                  >
                    Revoke Access
                  </Button>
                </div>
              ))
            ) : (
              <Typography>No users have access to this file.</Typography>
            )}
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setOpenSharedWithDialog(false)}>Close</Button>
          </DialogActions>
        </Dialog>
      </Container>
    </>
  );
};

export default UploadedFilesPage;
