import React, { useEffect, useState } from 'react';
import {
  Container,
  Typography,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Button,
  Alert,
  CircularProgress,
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import axiosInstance from '../api/axiosInstance';
import Header from '../components/Header';
import CryptoJS from 'crypto-js';

const AccessFilesPage = () => {
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchAccessFiles = async () => {
      try {
        const response = await axiosInstance.get('/api/current-access-files/');
        console.log('Fetched files:', response.data.files); // Debugging
        setFiles(response.data.files || []);
      } catch (err) {
        console.error('Error fetching files:', err); // Debugging
        setError('Failed to fetch accessible files.');
      } finally {
        setLoading(false);
      }
    };
    fetchAccessFiles();
  }, []);

  const handleDownload = async (encryptedFileId, fileName) => {
    try {
      console.log(`Fetching file with ID: ${encryptedFileId}`); // Debugging
      const response = await axiosInstance.get(`/api/access/${encryptedFileId}/`);

      // Extract encrypted content, key, and IV from the response
      const { encrypted_content, key, iv, file_name } = response.data;

      // Convert encrypted content, key, and IV to WordArray
      const encryptedContentWordArray = CryptoJS.enc.Hex.parse(encrypted_content);
      const aesKeyWordArray = CryptoJS.enc.Hex.parse(key);
      const aesIvWordArray = CryptoJS.enc.Hex.parse(iv);

      // Decrypt the content
      const decryptedContent = CryptoJS.AES.decrypt(
        { ciphertext: encryptedContentWordArray },
        aesKeyWordArray,
        {
          iv: aesIvWordArray,
          mode: CryptoJS.mode.CFB,
          padding: CryptoJS.pad.Pkcs7,
        }
      ).toString(CryptoJS.enc.Utf8);

      // Display decrypted content in a modal or alert
      console.log('Decrypted Content:', decryptedContent);

      // Optionally download the decrypted content as a file
      const blob = new Blob([decryptedContent], { type: 'text/plain' });
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', fileName || file_name || 'decrypted_file.txt');
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      window.URL.revokeObjectURL(url);

      alert('File decrypted and ready for download.');
    } catch (err) {
      console.error('Error fetching or decrypting file:', err.response?.data || err.message); // Debugging
      alert(err.response?.data?.error || 'Failed to decrypt the file. Please try again.');
    }
  };

  const handleView = (encryptedFileId) => {
    // Navigate to the view page using the encrypted file ID
    navigate(`/view/${encryptedFileId}`);
  };

  return (
    <>
      <Header />
      <Container maxWidth="md" sx={{ mt: 10 }}>
        <Typography variant="h4" gutterBottom>
          Accessible Files
        </Typography>

        {loading ? (
          <CircularProgress />
        ) : error ? (
          <Alert severity="error">{error}</Alert>
        ) : (
          <TableContainer component={Paper} sx={{ mt: 4 }}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>File</TableCell>
                  <TableCell>Shared By</TableCell>
                  <TableCell>Actions</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {files.map((file) => (
                  <TableRow key={file.encrypted_file_id}>
                    <TableCell>{file.file_name}</TableCell>
                    <TableCell>{file.shared_by}</TableCell>
                    <TableCell>
                      {file.view_permission && (
                        <Button
                          variant="text"
                          color="primary"
                          onClick={() => handleView(file.encrypted_file_id)}
                        >
                          View
                        </Button>
                      )}
                      {file.download_permission && (
                        <Button
                          variant="contained"
                          color="primary"
                          sx={{ ml: 2 }}
                          onClick={() =>
                            handleDownload(file.encrypted_file_id, file.file_name)
                          }
                        >
                          Download
                        </Button>
                      )}
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        )}
      </Container>
    </>
  );
};

export default AccessFilesPage;
