import React, { useState, useEffect } from 'react';
import {
  Container,
  Typography,
  Box,
  Button,
  CircularProgress,
  Alert,
} from '@mui/material';
import { CloudUpload as CloudUploadIcon } from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import CryptoJS from 'crypto-js';
import axios from 'axios';

const HomePage = () => {
  const [file, setFile] = useState(null); // Selected file
  const [fileContent, setFileContent] = useState(null); // File content as binary
  const [aesKey, setAESKey] = useState(''); // AES Key
  const [aesIv, setAESIv] = useState(''); // AES IV
  const [uploading, setUploading] = useState(false); // Uploading state
  const [success, setSuccess] = useState(false); // Success state
  const [error, setError] = useState(''); // Error state

  const navigate = useNavigate();

  // Generate AES Key and IV
  useEffect(() => {
    const generateAESKeyAndIV = () => {
      const key = CryptoJS.lib.WordArray.random(32).toString(CryptoJS.enc.Hex); // 256-bit key
      const iv = CryptoJS.lib.WordArray.random(16).toString(CryptoJS.enc.Hex); // 128-bit IV
      setAESKey(key);
      setAESIv(iv);
    };

    generateAESKeyAndIV();
  }, []);

  // Handle file selection
  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (!selectedFile) {
      setError('No file selected.');
      return;
    }

    setFile(selectedFile);
    setError('');
    setSuccess(false);

    const reader = new FileReader();
    reader.onload = () => {
      setFileContent(reader.result); // File content as binary (ArrayBuffer)
    };
    reader.readAsArrayBuffer(selectedFile); // Read file as binary
  };

  // Handle file upload
  const handleUpload = async () => {
    if (!fileContent) {
      setError('Please select a file before uploading.');
      return;
    }

    setUploading(true);
    setError('');
    setSuccess(false);

    try {
      // Encrypt the file content
      const key = CryptoJS.enc.Hex.parse(aesKey);
      const iv = CryptoJS.enc.Hex.parse(aesIv);
      const wordArray = CryptoJS.lib.WordArray.create(new Uint8Array(fileContent)); // Convert ArrayBuffer to WordArray

      const encryptedContent = CryptoJS.AES.encrypt(wordArray, key, {
        iv: iv,
        mode: CryptoJS.mode.CBC,
        padding: CryptoJS.pad.Pkcs7,
      }).ciphertext.toString(CryptoJS.enc.Base64); // Ensure base64 encoding for binary safety

      // Prepare payload
      const payload = {
        file_name: file.name,
        encrypted_content: encryptedContent,
        aes_key: aesKey,
        aes_iv: aesIv,
      };

      // Send to backend
      const response = await axios.post('/api/upload-file', payload);

      if (response.status === 200) {
        setSuccess(true);
      } else {
        setError('Upload failed. Please try again.');
      }
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to upload the file.');
    } finally {
      setUploading(false);
    }
  };

  return (
    <Container maxWidth="md" sx={{ mt: 10, textAlign: 'center' }}>
      <Typography variant="h4" gutterBottom>
        Secure File Upload
      </Typography>
      <Typography variant="body1" color="textSecondary" gutterBottom>
        Upload your files securely with AES-256 encryption.
      </Typography>

      {/* File Selection Section */}
      <Box
        sx={{
          mt: 4,
          p: 3,
          border: '1px dashed #ddd',
          borderRadius: 4,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          gap: 2,
          bgcolor: '#f9f9f9',
        }}
      >
        <Button
          variant="contained"
          component="label"
          startIcon={<CloudUploadIcon />}
          sx={{
            bgcolor: '#1976d2',
            '&:hover': {
              bgcolor: '#125ea8',
            },
          }}
        >
          Select File
          <input type="file" hidden onChange={handleFileChange} />
        </Button>

        {file && <Typography variant="body2">{file.name}</Typography>}

        <Button
          variant="contained"
          onClick={handleUpload}
          disabled={uploading || !file}
          sx={{
            mt: 2,
            bgcolor: '#4caf50',
            '&:hover': {
              bgcolor: '#388e3c',
            },
          }}
        >
          {uploading ? 'Uploading...' : 'Upload File'}
        </Button>

        {uploading && <CircularProgress size={24} sx={{ mt: 2 }} />}
      </Box>

      {/* Success/Error Messages */}
      {success && (
        <Alert severity="success" sx={{ mt: 4 }}>
          File uploaded successfully!
        </Alert>
      )}
      {error && (
        <Alert severity="error" sx={{ mt: 4 }}>
          {error}
        </Alert>
      )}

      {/* Navigation Buttons */}
      <Box sx={{ mt: 4, display: 'flex', justifyContent: 'center', gap: 2 }}>
        <Button variant="outlined" onClick={() => navigate('/uploaded-files')}>
          View Uploaded Files
        </Button>
        <Button variant="outlined" onClick={() => navigate('/current-access-files')}>
          View Current Access Files
        </Button>
      </Box>
    </Container>
  );
};

export default HomePage;
