import React, { useEffect, useState } from 'react';
import {
  Container,
  Typography,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Button,
  Alert,
  CircularProgress,
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import axiosInstance from '../api/axiosInstance';
import Header from '../components/Header';

const UploadedFilesPage = () => {
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUploadedFiles = async () => {
      try {
        const response = await axiosInstance.get('/api/all-files/');
        setFiles(response.data.files);
      } catch (err) {
        setError('Failed to fetch uploaded files.');
      } finally {
        setLoading(false);
      }
    };
    fetchUploadedFiles();
  }, []);

  const handleDownload = async (fileId) => {
    try {
      const response = await axiosInstance.get(`/api/access/${fileId}/`, {
        responseType: 'blob',
      });
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'file.encrypted');
      document.body.appendChild(link);
      link.click();
    } catch (err) {
      alert('Failed to download file.');
    }
  };

  const handleViewSharedWith = (fileId) => {
    navigate(`/shared-with/${fileId}`); // Redirect to SharedWithPage with fileId
  };

  return (
    <>
      <Header />
      <Container maxWidth="md" sx={{ mt: 10 }}>
        <Typography variant="h4" gutterBottom>
          Uploaded Files
        </Typography>

        {loading ? (
          <CircularProgress />
        ) : error ? (
          <Alert severity="error">{error}</Alert>
        ) : (
          <TableContainer component={Paper} sx={{ mt: 4 }}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>File Name</TableCell>
                  <TableCell>Uploaded At</TableCell>
                  <TableCell>Actions</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {files.map((file) => (
                  <TableRow key={file.id}>
                    <TableCell>{file.file_name}</TableCell>
                    <TableCell>{new Date(file.uploaded_at).toLocaleString()}</TableCell>
                    <TableCell>
                      <Button
                        variant="contained"
                        color="primary"
                        onClick={() => handleDownload(file.id)}
                      >
                        Download
                      </Button>
                      <Button
                        variant="text"
                        color="info"
                        sx={{ ml: 2 }}
                        onClick={() => handleViewSharedWith(file.id)}
                      >
                        Shared With
                      </Button>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        )}
      </Container>
    </>
  );
};

export default UploadedFilesPage;
