import React, { useState } from 'react';
import {
  Container,
  Typography,
  Box,
  Button,
  CircularProgress,
  Alert,
} from '@mui/material';
import { CloudUpload as CloudUploadIcon, FileCopy as FileCopyIcon } from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import { uploadFile } from '../api/fileApi'; // API helper for file upload
import Header from '../components/Header';
import CryptoJS from 'crypto-js';

const HomePage = () => {
  const [file, setFile] = useState(null); // Selected file
  const [encryptedFile, setEncryptedFile] = useState(null); // Encrypted file Blob
  const [uploading, setUploading] = useState(false); // Uploading state
  const [success, setSuccess] = useState(false); // Success state
  const [error, setError] = useState(''); // Error state

  const navigate = useNavigate(); // For navigation

  // Generate AES-256 Key and IV
  const generateAESKeyAndIV = () => {
    const key = CryptoJS.lib.WordArray.random(32).toString(CryptoJS.enc.Hex); // 256-bit key
    const iv = CryptoJS.lib.WordArray.random(16).toString(CryptoJS.enc.Hex); // 128-bit IV
    return { key, iv };
  };

  const { key: AES_KEY, iv: AES_IV } = generateAESKeyAndIV();

  // Handle file selection
  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (!selectedFile) return;

    setFile(selectedFile);
    setError('');
    setSuccess(false);

    const reader = new FileReader();
    reader.onload = () => {
      const fileContent = reader.result;
      try {
        // Encrypt the file content using AES-256
        const encrypted = CryptoJS.AES.encrypt(fileContent, AES_KEY, {
          iv: AES_IV,
          mode: CryptoJS.mode.CFB,
          padding: CryptoJS.pad.Pkcs7,
        }).toString();

        // Create an encrypted file Blob
        const encryptedBlob = new Blob([encrypted], { type: 'text/plain' });
        setEncryptedFile(encryptedBlob);

        // Debugging logs
        console.log('AES_KEY:', AES_KEY);
        console.log('AES_IV:', AES_IV);
      } catch (encryptionError) {
        setError('Failed to encrypt the file.');
        setFile(null);
        setEncryptedFile(null);
      }
    };
    reader.readAsText(selectedFile); // Read the file as text for encryption
  };

  // Handle file upload
  const handleUpload = async () => {
    if (!encryptedFile) {
      setError('Please select and encrypt a file before uploading.');
      return;
    }

    setUploading(true);
    setSuccess(false);
    setError('');

    const formData = new FormData();
    formData.append('file', encryptedFile, file.name); // Attach encrypted file
    formData.append('aes_key', AES_KEY); // Attach AES key
    formData.append('aes_iv', AES_IV); // Attach AES IV

    // Debugging: Log FormData keys and values
    for (let [key, value] of formData.entries()) {
      console.log(`${key}:`, value);
    }

    try {
      await uploadFile(formData); // Call the uploadFile API helper
      setSuccess(true);
    } catch (uploadError) {
      setError(uploadError.message || 'Failed to upload the encrypted file.');
    } finally {
      setUploading(false);
    }
  };

  return (
    <>
      <Header />
      <Container maxWidth="md" sx={{ mt: 10, textAlign: 'center' }}>
        <Typography variant="h4" gutterBottom>
          Secure File Upload
        </Typography>
        <Typography variant="body1" color="textSecondary" gutterBottom>
          Your files will be encrypted with AES-256 before uploading for enhanced security.
        </Typography>

    {/* File Upload Section */}
    <Box
          sx={{
            mt: 4,
            p: 3,
            border: '1px dashed #ddd',
            borderRadius: 4,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            gap: 2,
            bgcolor: '#f9f9f9',
          }}
        >
          <Button
            variant="contained"
            component="label"
            startIcon={<CloudUploadIcon />}
            sx={{
              bgcolor: '#1976d2',
              '&:hover': {
                bgcolor: '#125ea8',
              },
            }}
          >
            Select File
            <input type="file" hidden onChange={handleFileChange} />
          </Button>

          {file && <Typography variant="body2">{file.name}</Typography>}

          <Button
            variant="contained"
            onClick={handleUpload}
            disabled={uploading || !file}
            sx={{
              mt: 2,
              bgcolor: '#4caf50',
              '&:hover': {
                bgcolor: '#388e3c',
              },
            }}
          >
            {uploading ? 'Uploading...' : 'Upload File'}
          </Button>

          {uploading && <CircularProgress size={24} sx={{ mt: 2 }} />}
        </Box>

        {success && (
          <Alert severity="success" sx={{ mt: 4 }}>
            File uploaded successfully and securely!
          </Alert>
        )}
        {error && (
          <Alert severity="error" sx={{ mt: 4 }}>
            {error}
          </Alert>
        )}

        <Box sx={{ mt: 4, display: 'flex', justifyContent: 'center', gap: 2 }}>
          <Button
            variant="outlined"
            startIcon={<FileCopyIcon />}
            onClick={() => navigate('/uploaded-files')}
          >
            View Uploaded Files
          </Button>
          <Button
            variant="outlined"
            startIcon={<FileCopyIcon />}
            onClick={() => navigate('/current-access-files')}
          >
            View Current Access Files
          </Button>
        </Box>
      </Container>
    </>
  );
};

export default HomePage;
