import React, { useEffect, useState } from 'react';
import {
  Container,
  Typography,
  Button,
  Alert,
  CircularProgress,
  TextField,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Checkbox,
  Paper,
} from '@mui/material';
import { useParams } from 'react-router-dom';
import axiosInstance from '../api/axiosInstance';
import Header from '../components/Header'; // Import Header component

const SharedWithPage = () => {
  const { fileId } = useParams();
  const [sharedUsers, setSharedUsers] = useState([]);
  const [newUser, setNewUser] = useState('');
  const [permissions, setPermissions] = useState({ view: false, download: false });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [dialogOpen, setDialogOpen] = useState(false);
  const [addingUser, setAddingUser] = useState(false);

  useEffect(() => {
    const fetchSharedUsers = async () => {
      try {
        const response = await axiosInstance.get(`/api/shared-with/${fileId}/`);
        setSharedUsers(response.data.shared_users || []);
      } catch (err) {
        setError('Failed to fetch shared users.');
      } finally {
        setLoading(false);
      }
    };
    fetchSharedUsers();
  }, [fileId]);

  const handleAddUser = async () => {
    if (!newUser.trim()) {
      alert('Username cannot be empty.');
      return;
    }

    setAddingUser(true);
    try {
      await axiosInstance.post(`/api/share/${fileId}/`, {
        username: newUser.trim(),
        view_permission: permissions.view,
        download_permission: permissions.download,
      });

      alert('User added successfully.');
      setDialogOpen(false);
      setNewUser('');
      setPermissions({ view: false, download: false });

      // Update the shared users list
      setSharedUsers((prev) => [
        ...prev,
        { username: newUser.trim(), view_permission: permissions.view, download_permission: permissions.download },
      ]);
    } catch (err) {
      alert('Failed to add user. Please ensure the username is correct.');
    } finally {
      setAddingUser(false);
    }
  };

  const handleEditPermission = async (username, permissionType, value) => {
    try {
      await axiosInstance.post(`/api/update-permission/${fileId}/`, {
        username,
        permission_type: permissionType,
        value,
      });

      alert('Permission updated successfully.');
      setSharedUsers((prev) =>
        prev.map((user) =>
          user.username === username
            ? { ...user, [permissionType]: value }
            : user
        )
      );
    } catch (err) {
      alert('Failed to update permission.');
    }
  };

  const handleRevokeAccess = async (username) => {
    try {
      await axiosInstance.post(`/api/revoke/${fileId}/`, { username });

      alert('Access revoked successfully.');
      setSharedUsers((prev) => prev.filter((user) => user.username !== username));
    } catch (err) {
      alert('Failed to revoke access.');
    }
  };

  return (
    <>
      <Header /> {/* Add the Header component */}
      <Container maxWidth="md" sx={{ mt: 10 }}>
        <Typography variant="h4" gutterBottom>
          Shared With
        </Typography>

        {loading ? (
          <CircularProgress />
        ) : error ? (
          <Alert severity="error">{error}</Alert>
        ) : (
          <Paper sx={{ p: 2 }}>
            <Button variant="contained" color="primary" onClick={() => setDialogOpen(true)}>
              Add User
            </Button>
            {sharedUsers.length > 0 ? (
              sharedUsers.map((user) => (
                <div
                  key={user.username}
                  style={{
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'space-between',
                    marginTop: 16,
                  }}
                >
                  <Typography>
                    {user.username} ({user.email || 'No email available'})
                  </Typography>
                  <div>
                    <Checkbox
                      checked={user.view_permission}
                      onChange={(e) =>
                        handleEditPermission(user.username, 'view_permission', e.target.checked)
                      }
                    />
                    <Typography variant="caption">View</Typography>
                    <Checkbox
                      checked={user.download_permission}
                      onChange={(e) =>
                        handleEditPermission(user.username, 'download_permission', e.target.checked)
                      }
                    />
                    <Typography variant="caption">Download</Typography>
                    <Button
                      color="error"
                      variant="outlined"
                      onClick={() => handleRevokeAccess(user.username)}
                      sx={{ ml: 2 }}
                    >
                      Revoke
                    </Button>
                  </div>
                </div>
              ))
            ) : (
              <Typography>No users have access to this file.</Typography>
            )}
          </Paper>
        )}

        {/* Add User Dialog */}
        <Dialog open={dialogOpen} onClose={() => setDialogOpen(false)}>
          <DialogTitle>Add User</DialogTitle>
          <DialogContent>
            <TextField
              label="Username"
              fullWidth
              value={newUser}
              onChange={(e) => setNewUser(e.target.value)}
              sx={{ mb: 2 }}
              disabled={addingUser}
            />
            <div style={{ display: 'flex', alignItems: 'center', gap: 10 }}>
              <Checkbox
                checked={permissions.view}
                onChange={(e) => setPermissions((prev) => ({ ...prev, view: e.target.checked }))}
                disabled={addingUser}
              />
              View Permission
              <Checkbox
                checked={permissions.download}
                onChange={(e) => setPermissions((prev) => ({ ...prev, download: e.target.checked }))}
                disabled={addingUser}
              />
              Download Permission
            </div>
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setDialogOpen(false)} disabled={addingUser}>
              Cancel
            </Button>
            <Button onClick={handleAddUser} color="primary" disabled={addingUser}>
              {addingUser ? 'Adding...' : 'Add'}
            </Button>
          </DialogActions>
        </Dialog>
      </Container>
    </>
  );
};

export default SharedWithPage;
