// src/pages/LoginPage.js
import React, { useState, useContext } from 'react';
import {
  Box,
  Heading,
  Text,
  Button,
  FormControl,
  FormLabel,
  Input,
  VStack,
  Link,
  useDisclosure,
} from '@chakra-ui/react';
import { useNavigate } from 'react-router-dom';
import { loginUser } from '../api/authApi';
import { AuthContext } from '../contexts/AuthContext';

const LoginPage = () => {
  const navigate = useNavigate();
  const { saveTokens } = useContext(AuthContext);

  const [credentials, setCredentials] = useState({ username: '', password: '' });
  const [loading, setLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCredentials({ ...credentials, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setErrorMessage('');

    try {
      const data = await loginUser(credentials);
      saveTokens(data.access, data.refresh);
      alert('Login successful! Redirecting...'); // Use `alert()` as a simple fallback
      navigate('/');
    } catch (error) {
      const errorMsg = error.response?.data?.detail || 'Something went wrong!';
      setErrorMessage(errorMsg);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Box
      maxW="400px"
      mx="auto"
      mt="10vh"
      p="6"
      boxShadow="lg"
      borderRadius="lg"
      bg="white"
    >
      <Heading mb="4" textAlign="center" color="teal.500">
        Welcome Back
      </Heading>
      <Text mb="6" textAlign="center" color="gray.600">
        Login to your account
      </Text>
      <form onSubmit={handleSubmit}>
        <VStack spacing={4}>
          <FormControl>
            <FormLabel>Username</FormLabel>
            <Input
              type="text"
              name="username"
              placeholder="Enter your username"
              value={credentials.username}
              onChange={handleChange}
              required
            />
          </FormControl>
          <FormControl>
            <FormLabel>Password</FormLabel>
            <Input
              type="password"
              name="password"
              placeholder="Enter your password"
              value={credentials.password}
              onChange={handleChange}
              required
            />
          </FormControl>
          {errorMessage && (
            <Text color="red.500" fontSize="sm">
              {errorMessage}
            </Text>
          )}
          <Button
            colorScheme="teal"
            w="full"
            type="submit"
            isLoading={loading}
          >
            Sign In
          </Button>
          <Button
            variant="link"
            colorScheme="teal"
            onClick={() => navigate('/forgot-password')}
          >
            Forgot Password?
          </Button>
          <Text>
            Don't have an account?{' '}
            <Link color="teal.500" onClick={() => navigate('/register')}>
              Register
            </Link>
          </Text>
        </VStack>
      </form>
    </Box>
  );
};

export default LoginPage;
