import React, { createContext, useState, useEffect } from 'react';

// Create the AuthContext and export it
export const AuthContext = createContext(null);

export const AuthProvider = ({ children }) => {
  const [accessToken, setAccessToken] = useState(localStorage.getItem('access_token') || null);
  const [refreshToken, setRefreshToken] = useState(localStorage.getItem('refresh_token') || null);
  const [user, setUser] = useState(null); // Optional: store user info (like username, role, etc.)

  useEffect(() => {
    // Optionally, parse user details from the token or store separately
    // setUser(...)
  }, [accessToken]);

  // Save tokens to localStorage for persistent login
  const saveTokens = (access, refresh) => {
    localStorage.setItem('access_token', access);
    localStorage.setItem('refresh_token', refresh);
    setAccessToken(access);
    setRefreshToken(refresh);
  };

  // Remove tokens from localStorage
  const clearTokens = () => {
    localStorage.removeItem('access_token');
    localStorage.removeItem('refresh_token');
    setAccessToken(null);
    setRefreshToken(null);
    setUser(null);
  };

  const value = {
    accessToken,
    refreshToken,
    user,
    setUser,
    saveTokens,
    clearTokens,
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};
