import axiosInstance from './axiosInstance';

// REGISTER: POST /api/register/
export const registerUser = async (userData) => {
  // userData -> { username, password, email, role? }
  const response = await axiosInstance.post('/api/register/', userData);
  return response.data;
};

// LOGIN: POST /api/login/ (using Django rest_framework_simplejwt)
export const loginUser = async (credentials) => {
  // credentials -> { username, password }
  const response = await axiosInstance.post('/api/login/', credentials);
  return response.data; // typically returns { refresh, access }
};

// LOGOUT: POST /api/logout/
export const logoutUser = async (refreshToken) => {
  // refreshToken -> e.g. localStorage.getItem('refresh_token')
  const response = await axiosInstance.post('/api/logout/', {
    refresh: refreshToken,
  });
  return response.data;
};

// VERIFY EMAIL: GET /api/verify-email/?token=<token>
export const verifyEmail = async (token) => {
  const response = await axiosInstance.get(`/api/verify-email/?token=${token}`);
  return response.data;
};
