// src/components/Header.js
import React, { useContext, useEffect, useState } from 'react';
import { AppBar, Toolbar, Typography, Avatar, Box, Button } from '@mui/material';
import { AuthContext } from '../contexts/AuthContext';
import { getUserDetails } from '../api/userApi';
import { logoutUser } from '../api/authApi'; // Ensure this import is added
import { useNavigate } from 'react-router-dom';

const Header = () => {
  const [user, setUser] = useState(null);
  const { refreshToken, clearTokens } = useContext(AuthContext);
  const navigate = useNavigate();

  // Fetch user details on mount
  useEffect(() => {
    const fetchUserDetails = async () => {
      try {
        const response = await getUserDetails();
        setUser(response.data);
      } catch (error) {
        console.error('Failed to fetch user details:', error);
      }
    };

    if (refreshToken) {
      fetchUserDetails();
    }
  }, [refreshToken]);

  const handleLogout = async () => {
    try {
      if (refreshToken) {
        await logoutUser(refreshToken); // Call logout API
      }
    } catch (error) {
      console.error('Logout failed:', error);
    } finally {
      clearTokens();
      navigate('/login');
    }
  };

  return (
    <AppBar
      position="fixed"
      sx={{ bgcolor: '#2E3B55', width: '100%' }}
    >
      <Toolbar sx={{ display: 'flex', justifyContent: 'space-between' }}>
        {/* Left Section: User Info */}
        {user && (
          <Box sx={{ display: 'flex', alignItems: 'center' }}>
            <Avatar sx={{ bgcolor: '#FF7043', mr: 2 }}>
              {user.username[0].toUpperCase()}
            </Avatar>
            <Box>
              <Typography variant="body1" sx={{ fontWeight: 'bold', color: '#FFF' }}>
                Hi, {user.Name}
              </Typography>
              <Typography variant="caption" sx={{ color: '#D1D9E6' }}>
                @{user.username}
              </Typography>
            </Box>
          </Box>
        )}

        {/* Right Section: Logout Button */}
        <Button
          variant="contained"
          color="error"
          onClick={handleLogout}
          sx={{
            textTransform: 'none',
            fontWeight: 'bold',
          }}
        >
          Logout
        </Button>
      </Toolbar>
    </AppBar>
  );
};

export default Header;
