import React, { useEffect, useState } from 'react';
import {
  Container,
  Typography,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Button,
  Alert,
  CircularProgress,
} from '@mui/material';
import axiosInstance from '../api/axiosInstance';
import Header from '../components/Header';

const AccessFilesPage = () => {
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchAccessFiles = async () => {
      try {
        const response = await axiosInstance.get('/api/current-access-files/');
        // Transform API response to match expected structure
        const transformedFiles = response.data.files.map((file) => ({
          id: file.file__id,
          file_name: file.file__file_name,
          uploaded_at: file.file__uploaded_at,
          shared_by: file.shared_by || 'Unknown', // Handle missing shared_by
        }));
        setFiles(transformedFiles);
      } catch (err) {
        setError('Failed to fetch accessible files.');
      } finally {
        setLoading(false);
      }
    };
    fetchAccessFiles();
  }, []);

  const handleDownload = async (fileId, fileName) => {
    try {
      const response = await axiosInstance.get(`/api/access/${fileId}/`, {
        responseType: 'blob',
      });
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', fileName || 'file.encrypted');
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    } catch (err) {
      alert('Failed to download file. Please try again.');
    }
  };

  return (
    <>
      <Header />
      <Container maxWidth="md" sx={{ mt: 10 }}>
        <Typography variant="h4" gutterBottom>
          Accessible Files
        </Typography>

        {loading ? (
          <CircularProgress />
        ) : error ? (
          <Alert severity="error">{error}</Alert>
        ) : (
          <TableContainer component={Paper} sx={{ mt: 4 }}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>File Name</TableCell>
                  <TableCell>Shared By</TableCell>
                  <TableCell>Actions</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {files.map((file) => (
                  <TableRow key={file.id}>
                    <TableCell>{file.file_name}</TableCell>
                    <TableCell>{file.shared_by}</TableCell>
                    <TableCell>
                      <Button
                        variant="contained"
                        color="primary"
                        sx={{ ml: 2 }}
                        onClick={() => handleDownload(file.id, file.file_name)}
                      >
                        Download
                      </Button>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        )}
      </Container>
    </>
  );
};

export default AccessFilesPage;
