import React, { useEffect, useState } from 'react';
import {
  Container,
  Typography,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Button,
  Alert,
  CircularProgress,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Checkbox,
} from '@mui/material';
import axiosInstance from '../api/axiosInstance';
import Header from '../components/Header';

const UploadedFilesPage = () => {
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [sharedWithDialog, setSharedWithDialog] = useState(false);
  const [sharedUsers, setSharedUsers] = useState([]);
  const [selectedFileId, setSelectedFileId] = useState(null);

  useEffect(() => {
    const fetchUploadedFiles = async () => {
      try {
        const response = await axiosInstance.get('/api/all-files/');
        setFiles(response.data.files);
      } catch (err) {
        setError('Failed to fetch uploaded files.');
      } finally {
        setLoading(false);
      }
    };
    fetchUploadedFiles();
  }, []);

  const handleDownload = async (fileId, fileName) => {
    try {
      const response = await axiosInstance.get(`/api/access/${fileId}/`, {
        responseType: 'blob',
      });
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', fileName || 'file.encrypted');
      document.body.appendChild(link);
      link.click();
    } catch (err) {
      alert('Failed to download file.');
    }
  };

  const handleViewSharedWith = async (fileId) => {
    try {
      setSelectedFileId(fileId);
      const response = await axiosInstance.get(`/api/shared-with/${fileId}/`);
      setSharedUsers(response.data.shared_users || []);
      setSharedWithDialog(true);
    } catch (err) {
      alert('Failed to fetch shared users.');
    }
  };

  const handleEditPermission = async (username, permissionType, value) => {
    try {
      await axiosInstance.post(`/api/update-permission/${selectedFileId}/`, {
        username,
        permission_type: permissionType,
        value,
      });
      alert('Permission updated successfully.');
      handleViewSharedWith(selectedFileId); // Refresh the shared users list
    } catch (err) {
      alert('Failed to update permission.');
    }
  };

  const handleRevokeAccess = async (username) => {
    try {
      await axiosInstance.post(`/api/revoke/${selectedFileId}/`, {
        username,
      });
      alert('Access revoked successfully.');
      handleViewSharedWith(selectedFileId); // Refresh the shared users list
    } catch (err) {
      alert('Failed to revoke access.');
    }
  };

  return (
    <>
      <Header />
      <Container maxWidth="md" sx={{ mt: 10 }}>
        <Typography variant="h4" gutterBottom>
          Uploaded Files
        </Typography>

        {loading ? (
          <CircularProgress />
        ) : error ? (
          <Alert severity="error">{error}</Alert>
        ) : (
          <TableContainer component={Paper} sx={{ mt: 4 }}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>File Name</TableCell>
                  <TableCell>Uploaded At</TableCell>
                  <TableCell>Actions</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {files.map((file) => (
                  <TableRow key={file.id}>
                    <TableCell>{file.file_name}</TableCell>
                    <TableCell>{new Date(file.uploaded_at).toLocaleString()}</TableCell>
                    <TableCell>
                      <Button
                        variant="contained"
                        color="primary"
                        onClick={() => handleDownload(file.id, file.file_name)}
                      >
                        Download
                      </Button>
                      <Button
                        variant="outlined"
                        color="secondary"
                        sx={{ ml: 2 }}
                        onClick={() => handleViewSharedWith(file.id)}
                      >
                        Shared With
                      </Button>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        )}

        {/* Shared With Dialog */}
        <Dialog open={sharedWithDialog} onClose={() => setSharedWithDialog(false)} maxWidth="sm" fullWidth>
          <DialogTitle>Shared With</DialogTitle>
          <DialogContent>
            {sharedUsers.length > 0 ? (
              sharedUsers.map((user) => (
                <div
                  key={user.username}
                  style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginBottom: 16 }}
                >
                  <Typography>
                    {user.username} ({user.email})
                  </Typography>
                  <div>
                    <Checkbox
                      checked={user.view_permission}
                      onChange={(e) =>
                        handleEditPermission(user.username, 'view_permission', e.target.checked)
                      }
                    />
                    <Typography variant="caption">View</Typography>
                    <Checkbox
                      checked={user.download_permission}
                      onChange={(e) =>
                        handleEditPermission(user.username, 'download_permission', e.target.checked)
                      }
                    />
                    <Typography variant="caption">Download</Typography>
                    <Button
                      color="error"
                      variant="outlined"
                      onClick={() => handleRevokeAccess(user.username)}
                      sx={{ ml: 2 }}
                    >
                      Revoke
                    </Button>
                  </div>
                </div>
              ))
            ) : (
              <Typography>No users have access to this file.</Typography>
            )}
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setSharedWithDialog(false)}>Close</Button>
          </DialogActions>
        </Dialog>
      </Container>
    </>
  );
};

export default UploadedFilesPage;
