import React, { useEffect, useState } from 'react';
import {
  Container,
  Typography,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Button,
  TextField,
  Alert,
  CircularProgress,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import axiosInstance from '../api/axiosInstance';
import Header from '../components/Header';

const UploadedFilesPage = () => {
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [openShareDialog, setOpenShareDialog] = useState(false);
  const [selectedFileId, setSelectedFileId] = useState(null);
  const [shareDetails, setShareDetails] = useState({
    user_id: '',
    view_permission: false,
    download_permission: false,
    expiration: 24, // default expiration in hours
  });
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUploadedFiles = async () => {
      try {
        const response = await axiosInstance.get('/api/all-files/');
        setFiles(response.data.files);
      } catch (err) {
        setError('Failed to fetch uploaded files.');
      } finally {
        setLoading(false);
      }
    };
    fetchUploadedFiles();
  }, []);

  const handleDownload = async (fileId) => {
    try {
      const response = await axiosInstance.get(`/api/access/${fileId}/`, {
        responseType: 'blob',
      });
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'file.encrypted');
      document.body.appendChild(link);
      link.click();
    } catch (err) {
      alert('Failed to download file.');
    }
  };

  const openShareDialogHandler = (fileId) => {
    setSelectedFileId(fileId);
    setOpenShareDialog(true);
  };

  const handleShareFile = async () => {
    try {
      const response = await axiosInstance.post(`/api/share/${selectedFileId}/`, shareDetails);
      const successMessage = response.data.message || 'File shared successfully.';
      alert(successMessage);
      setOpenShareDialog(false);
      setShareDetails({
        user_id: '',
        view_permission: false,
        download_permission: false,
        expiration: 24,
      });
    } catch (err) {
      const errorMessage =
        err.response?.data?.error || 'Failed to share file. Please try again.';
      alert(errorMessage);
    }
  };

  const handleDialogClose = () => {
    setOpenShareDialog(false);
    setShareDetails({
      user_id: '',
      view_permission: false,
      download_permission: false,
      expiration: 24,
    });
  };

  const handleViewSharedWith = (fileId) => {
    navigate(`/shared-with/${fileId}`); // Redirect to SharedWithPage with fileId
  };

  return (
    <>
      <Header />
      <Container maxWidth="md" sx={{ mt: 10 }}>
        <Typography variant="h4" gutterBottom>
          Uploaded Files
        </Typography>

        {loading ? (
          <CircularProgress />
        ) : error ? (
          <Alert severity="error">{error}</Alert>
        ) : (
          <TableContainer component={Paper} sx={{ mt: 4 }}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>File Name</TableCell>
                  <TableCell>Uploaded At</TableCell>
                  <TableCell>Actions</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {files.map((file) => (
                  <TableRow key={file.id}>
                    <TableCell>{file.file_name}</TableCell>
                    <TableCell>{new Date(file.uploaded_at).toLocaleString()}</TableCell>
                    <TableCell>
                      <Button
                        variant="contained"
                        color="primary"
                        onClick={() => handleDownload(file.id)}
                      >
                        Download
                      </Button>
                      <Button
                        variant="outlined"
                        color="secondary"
                        sx={{ ml: 2 }}
                        onClick={() => openShareDialogHandler(file.id)}
                      >
                        Share
                      </Button>
                      <Button
                        variant="text"
                        color="info"
                        sx={{ ml: 2 }}
                        onClick={() => handleViewSharedWith(file.id)}
                      >
                        Shared With
                      </Button>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        )}

        {/* Share File Dialog */}
        <Dialog open={openShareDialog} onClose={handleDialogClose}>
          <DialogTitle>Share File</DialogTitle>
          <DialogContent>
            <TextField
              label="User ID"
              fullWidth
              value={shareDetails.user_id}
              onChange={(e) => setShareDetails({ ...shareDetails, user_id: e.target.value })}
              sx={{ mt: 2 }}
            />
            <TextField
              label="Expiration (hours)"
              fullWidth
              type="number"
              value={shareDetails.expiration}
              onChange={(e) => setShareDetails({ ...shareDetails, expiration: e.target.value })}
              sx={{ mt: 2 }}
            />
            <div style={{ display: 'flex', justifyContent: 'space-between', marginTop: 16 }}>
              <Button
                variant={shareDetails.view_permission ? 'contained' : 'outlined'}
                onClick={() =>
                  setShareDetails({
                    ...shareDetails,
                    view_permission: !shareDetails.view_permission,
                  })
                }
              >
                {shareDetails.view_permission ? 'View Enabled' : 'Enable View'}
              </Button>
              <Button
                variant={shareDetails.download_permission ? 'contained' : 'outlined'}
                onClick={() =>
                  setShareDetails({
                    ...shareDetails,
                    download_permission: !shareDetails.download_permission,
                  })
                }
              >
                {shareDetails.download_permission ? 'Download Enabled' : 'Enable Download'}
              </Button>
            </div>
          </DialogContent>
          <DialogActions>
            <Button onClick={handleDialogClose} color="secondary">
              Cancel
            </Button>
            <Button onClick={handleShareFile} color="primary">
              Share
            </Button>
          </DialogActions>
        </Dialog>
      </Container>
    </>
  );
};

export default UploadedFilesPage;
