import React, { useEffect, useState } from 'react';
import {
  Container,
  Typography,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Button,
  Alert,
  CircularProgress,
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import axiosInstance from '../api/axiosInstance';
import Header from '../components/Header';

const AccessFilesPage = () => {
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchAccessFiles = async () => {
      try {
        const response = await axiosInstance.get('/api/current-access-files/');
        setFiles(response.data.files || []);
      } catch (err) {
        setError('Failed to fetch accessible files.');
      } finally {
        setLoading(false);
      }
    };
    fetchAccessFiles();
  }, []);

  const handleView = (encryptedFileId) => {
    // Navigate to the view page using the encrypted file ID
    navigate(`/view/${encryptedFileId}`);
  };

  const handleDownload = async (encryptedFileId, fileName) => {
    try {
      const response = await axiosInstance.get(`/api/access-shared-file/${encryptedFileId}/`, {
        responseType: 'blob',
      });
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', fileName || 'file.encrypted');
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    } catch (err) {
      alert('Failed to download file. Please try again.');
    }
  };

  return (
    <>
      <Header />
      <Container maxWidth="md" sx={{ mt: 10 }}>
        <Typography variant="h4" gutterBottom>
          Accessible Files
        </Typography>

        {loading ? (
          <CircularProgress />
        ) : error ? (
          <Alert severity="error">{error}</Alert>
        ) : (
          <TableContainer component={Paper} sx={{ mt: 4 }}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>File</TableCell>
                  <TableCell>Shared By</TableCell>
                  <TableCell>Actions</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {files.map((file) => (
                  <TableRow key={file.encrypted_file_id}>
                    <TableCell>Hidden File Name</TableCell> {/* Hide sensitive info */}
                    <TableCell>{file.shared_by}</TableCell>
                    <TableCell>
                      {file.view_permission && (
                        <Button
                          variant="text"
                          color="primary"
                          onClick={() => handleView(file.encrypted_file_id)}
                        >
                          View
                        </Button>
                      )}
                      {file.download_permission && (
                        <Button
                          variant="contained"
                          color="primary"
                          sx={{ ml: 2 }}
                          onClick={() =>
                            handleDownload(file.encrypted_file_id, file.file_name)
                          }
                        >
                          Download
                        </Button>
                      )}
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        )}
      </Container>
    </>
  );
};

export default AccessFilesPage;
