import React, { useEffect, useState } from 'react';
import {
  Container,
  Typography,
  Button,
  Alert,
  CircularProgress,
  TextField,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Checkbox,
  Paper,
} from '@mui/material';
import { useParams } from 'react-router-dom';
import axiosInstance from '../api/axiosInstance';
import Header from '../components/Header';

const SharedWithPage = () => {
  const { fileId } = useParams();
  const [sharedUsers, setSharedUsers] = useState([]);
  const [newUser, setNewUser] = useState('');
  const [permissions, setPermissions] = useState({ view: false, download: false });
  const [expiration, setExpiration] = useState(24); // Default expiration in hours
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [dialogOpen, setDialogOpen] = useState(false);

  useEffect(() => {
    const fetchSharedUsers = async () => {
      try {
        const response = await axiosInstance.get(`/api/shared-with/${fileId}/`);
        setSharedUsers(response.data.shared_users || []);
      } catch (err) {
        setError('Failed to fetch shared users.');
      } finally {
        setLoading(false);
      }
    };
    fetchSharedUsers();
  }, [fileId]);

  const handleAddUser = async () => {
    if (!newUser.trim()) {
      alert('Username cannot be empty.');
      return;
    }
    try {
      const response = await axiosInstance.post(`/api/share/${fileId}/`, {
        username: newUser.trim(),
        view_permission: permissions.view,
        download_permission: permissions.download,
        expiration, // Send expiration time in hours
      });
      alert(response.data.message);
      setDialogOpen(false);
      setNewUser('');
      setPermissions({ view: false, download: false });
      setExpiration(24); // Reset expiration
      // Add the new shared user to the list
      setSharedUsers((prev) => [...prev, response.data.shared_user]);
    } catch (err) {
      alert('Failed to add user. Please ensure the username is correct.');
    }
  };

  return (
    <>
      <Header />
      <Container maxWidth="md" sx={{ mt: 10 }}>
        <Typography variant="h4" gutterBottom>
          Shared With
        </Typography>

        {loading ? (
          <CircularProgress />
        ) : error ? (
          <Alert severity="error">{error}</Alert>
        ) : (
          <Paper sx={{ p: 2 }}>
            <Button variant="contained" color="primary" onClick={() => setDialogOpen(true)}>
              Add User
            </Button>
            {sharedUsers.length > 0 ? (
              sharedUsers.map((user) => (
                <div
                  key={user.username}
                  style={{
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'space-between',
                    marginTop: 16,
                  }}
                >
                  <Typography>
                    {user.username} ({user.email || 'No email available'})
                  </Typography>
                  <Typography variant="caption">
                    Expiration: {new Date(user.expiration).toLocaleString()}
                  </Typography>
                  <div>
                    <Checkbox
                      checked={user.view_permission}
                      onChange={(e) =>
                        handleEditPermission(user.username, 'view_permission', e.target.checked)
                      }
                    />
                    <Typography variant="caption">View</Typography>
                    <Checkbox
                      checked={user.download_permission}
                      onChange={(e) =>
                        handleEditPermission(user.username, 'download_permission', e.target.checked)
                      }
                    />
                    <Typography variant="caption">Download</Typography>
                  </div>
                </div>
              ))
            ) : (
              <Typography>No users have access to this file.</Typography>
            )}
          </Paper>
        )}

        {/* Add User Dialog */}
        <Dialog open={dialogOpen} onClose={() => setDialogOpen(false)}>
          <DialogTitle>Add User</DialogTitle>
          <DialogContent>
            <TextField
              label="Username"
              fullWidth
              value={newUser}
              onChange={(e) => setNewUser(e.target.value)}
              sx={{ mb: 2 }}
            />
            <TextField
              label="Expiration Time (hours)"
              type="number"
              fullWidth
              value={expiration}
              onChange={(e) => setExpiration(e.target.value)}
              sx={{ mb: 2 }}
            />
            <Checkbox
              checked={permissions.view}
              onChange={(e) => setPermissions((prev) => ({ ...prev, view: e.target.checked }))}
            />
            View Permission
            <Checkbox
              checked={permissions.download}
              onChange={(e) => setPermissions((prev) => ({ ...prev, download: e.target.checked }))}
            />
            Download Permission
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setDialogOpen(false)}>Cancel</Button>
            <Button onClick={handleAddUser} color="primary">
              Add
            </Button>
          </DialogActions>
        </Dialog>
      </Container>
    </>
  );
};

export default SharedWithPage;
